import{_ as t,c as r,o as a,ae as n}from"./chunks/framework.B_lm770W.js";const g=JSON.parse('{"title":"Tree View 生态总览","description":"","frontmatter":{},"headers":[],"relativePath":"tree-view/index.md","filePath":"tree-view/index.md","lastUpdated":1764933763000}'),i={name:"tree-view/index.md"};function o(d,e,s,l,h,c){return a(),r("div",null,[...e[0]||(e[0]=[n('<h1 id="tree-view-生态总览" tabindex="-1">Tree View 生态总览 <a class="header-anchor" href="#tree-view-生态总览" aria-label="Permalink to &quot;Tree View 生态总览&quot;">​</a></h1><p>Tree View 章节记录 VS Code 中“Explorer / 自定义视图”生态的事实需求与设计准则。该章节帮助读者在阅读单个 SRS/SDD 前，先掌握 Tree View 作为 UI/数据总线的全貌：它如何接收扩展的树形数据、如何被 Workbench 承载、又如何和命令、上下文、配置与深链协作。</p><h2 id="能力范围" tabindex="-1">能力范围 <a class="header-anchor" href="#能力范围" aria-label="Permalink to &quot;能力范围&quot;">​</a></h2><ul><li><strong>展示域</strong>：Tree View 自身的注册、渲染与缓存机制；由 <code>TreeDataProvider</code> + <code>TreeItem</code> 驱动。</li><li><strong>承载域</strong>：Workbench View Container（Explorer、Run&amp;Debug 等）提供生命周期、布局与状态持久化。</li><li><strong>交互域</strong>：命令、菜单、快捷键与 URI Handler 为 Tree Item、标题栏与节点右键提供行为。</li><li><strong>治理域</strong>：激活事件、Context Key、Configuration/Settings 用于驱动显隐与权限。</li></ul><p>该章节将上述四个域串成一致的执行链：<code>Manifest &amp; Activation → Provider &amp; State Machine → 命令/配置/URI → 观测与回退</code>。</p><h2 id="核心模型速览" tabindex="-1">核心模型速览 <a class="header-anchor" href="#核心模型速览" aria-label="Permalink to &quot;核心模型速览&quot;">​</a></h2><table tabindex="0"><thead><tr><th>模型/组件</th><th>职责</th><th>关联文档</th></tr></thead><tbody><tr><td>TreeDataProvider / TreeItem / TreeView</td><td>负责数据拉取、节点唯一标识、刷新与可见状态；是所有 SRS/SDD 的根模型。</td><td><a href="./tree-view-srs.html">Tree View SRS</a> / <a href="./tree-view-sdd.html">Tree View SDD</a></td></tr><tr><td>View Container / View Descriptor Service</td><td>为 Tree View 注册容器、控制布局、托管状态与活跃度。</td><td><a href="./workbench-views-and-containers-srs.html">Workbench 视图与容器 SRS</a> / <a href="./workbench-views-and-containers-sdd.html">SDD</a></td></tr><tr><td>Command Service / Menu Registry / Keybinding Resolver</td><td>建立 Tree Item 与命令之间的契约，维护 when clause、焦点与快捷键链路。</td><td><a href="./commands-menus-and-keybindings-srs.html">Commands/Menus/Keybindings SRS</a> / <a href="./commands-menus-and-keybindings-sdd.html">SDD</a></td></tr><tr><td>Activation Events / Context Key Service</td><td>控制何时激活 Provider、如何派发上下文键并同步至菜单与配置。</td><td><a href="./activation-and-context-system-srs.html">Activation &amp; Context SRS</a> / <a href="./activation-and-context-system-sdd.html">SDD</a></td></tr><tr><td>Configuration/Settings 系统</td><td>为 Tree View 提供 schema、监听与配置驱动的显隐、排序、远端参数。</td><td><a href="./configuration-and-settings-srs.html">Configuration &amp; Settings SRS</a> / <a href="./configuration-and-settings-sdd.html">SDD</a></td></tr><tr><td>URI Handler</td><td>将外部链接、深链或多窗口导航映射为 Tree View 的 <code>reveal</code>/<code>command</code> 行为。</td><td><a href="./uri-handler-and-deep-links-srs.html">URI Handler &amp; Deep Links SRS</a> / <a href="./uri-handler-and-deep-links-sdd.html">SDD</a></td></tr></tbody></table><h2 id="运行基线" tabindex="-1">运行基线 <a class="header-anchor" href="#运行基线" aria-label="Permalink to &quot;运行基线&quot;">​</a></h2><ol><li><strong>声明 → 激活</strong>：扩展在 <code>package.json</code> 声明 Tree View、激活事件与依赖的 Context/Configuration；Workbench 依据激活条件创建/回收 Provider。</li><li><strong>数据与可视化</strong>：<code>TreeDataProvider</code> 负责增量刷新、<code>TreeItem</code> 定义外观/交互，View Container 管理折叠、筛选与状态持久化。</li><li><strong>交互闭环</strong>：命令与菜单接受 Tree Item 上下文，快捷键与 when clause 联动；配置与 URI Handler 则为 Tree View 提供扩展入口。</li><li><strong>诊断与回退</strong>：章节中的 SRS 将列出日志、遥测、错误码与回退能力；SDD 提供实现建议与模式。</li></ol><h2 id="文档映射" tabindex="-1">文档映射 <a class="header-anchor" href="#文档映射" aria-label="Permalink to &quot;文档映射&quot;">​</a></h2><ul><li><strong>基线 SRS / SDD</strong>：Tree View 主域阐述 API 契约、用例、刷新策略与错误语义。</li><li><strong>承载与布局</strong>：Workbench 章节说明容器、分组、拖拽、焦点竞态。</li><li><strong>交互系统</strong>：Commands/Menus/Keybindings 章节解释命令总线、上下文键与键盘体验。</li><li><strong>激活与治理</strong>：Activation/Context + Configuration/Settings 描述显隐策略、延迟激活、配置驱动能力。</li><li><strong>跨入口</strong>：URI Handler 章节给出深链、外部唤起与安全约束。</li></ul><p>阅读顺序建议：先通读本页与 Tree View 主域 SRS，随后按“承载 → 交互 → 治理 → 深链”的顺序深入即可。</p>',12)])])}const S=t(i,[["render",o]]);export{g as __pageData,S as default};
